@Composable
fun pxToMm(px: Int): Float = with(LocalDensity.current) {
    val dpi = density * 160f
    px / dpi * 25.4f
}

@Composable
fun mmToPx(mm: Float): Int = with(LocalDensity.current) {
    val dpi = density * 160f
    (mm / 25.4f * dpi).roundToInt()
}

